"parseSFF_XML" <- function(filename){	tab <- readLines(filename)	tab <- tab[-c(1:2)]	tab <- tab[-c(length(tab))]	tab <- sub(" +","",tab)	tstart <- grep("<trace>",tab)	tstart <- c(tstart,length(tab)+1)	difference <- diff(tstart)	index <- rep(seq.int(1,length(difference)),as.vector(difference))	res <- tapply(seq.int(1,length(index)),index, function(x) {		tmp <- tab[x]		tind <- grep("<trace_name>",tmp)		lind <- grep("<clip_vector_left>",tmp)		rind <- grep("<clip_vector_right>",tmp)		tmp <- sub("<trace_name>","",tmp)		tmp <- sub("</trace_name>","",tmp)		tmp <- sub("<clip_vector_left>","",tmp)		tmp <- sub("</clip_vector_left>","",tmp)		tmp <- sub("<clip_vector_right>","",tmp)		tmp <- sub("</clip_vector_right>","",tmp)		restmp <- list()		restmp$Acc <- tmp[tind]		restmp$LC <- ifelse(length(lind) == 0,NA,tmp[lind])		restmp$RC <- ifelse(length(rind) == 0,NA,tmp[rind])		restmp	})	res <- matrix(unlist(res),ncol=3,byrow=T)	res <- as.data.frame(res)	res[,2] <- as.numeric(as.character(res[,2]))	res[,3] <- as.numeric(as.character(res[,3]))	colnames(res) <- c("Acc","LC","RC")	res}		